mapper.select:
SELECT TUI.USER_NAME, TUI.USER_ID, TUI.COMPANY_ID, TCI.COMPANY_NAME, TUI.USER_CLASS,
TUI.EMAIL, TUI.PHONE, TUI.USE_YN as useruseYn FROM T_USER_INFO TUI LEFT JOIN T_COMPANY_INFO TCI ON (TUI.COMPANY_ID = TCI.COMPANY_ID) WHERE
TUI.USER_ID = #{userId}

mapper.update:
UPDATE T_USER_INFO
SET  USER_PW = #{userPw}, PW_CH ='N'
WHERE USER_ID = #{userId}

UPDATE T_GROUP_CODE
SET GROUP_CODE = #{groupCode},
GROUP_NAME = #{groupName},
USE_YN = #{groupuseYn}
WHERE GROUP_CODE = #{groupCode}

mapper.insert:
INSERT INTO T_GROUP_AUTH(
GROUP_CODE,
USER_ID,
USER_NAME
)VALUES (
#{groupCode},
#{userId},
#{userName}
)

mapper.update:
UPDATE T_GROUP_AUTH
SET GROUP_CODE = #{groupCode},
USER_ID = #{userId},
USER_NAME = #{userName}
WHERE GROUP_ID = #{groupId}
