mapper.select:
SELECT TICKET_ID, PRIORITY_YN, COMPANY_NAME, STATUS_CD, REQUEST_TYPE_CD, SUPPORT_CD, IFNULL(MD, '') as MD, TITLE, T.CREATE_NAME, U.USER_NAME, DATE_FORMAT(T.CREATE_DT, '%Y-%m-%d') AS CREATE_DT, IFNULL(DATE_FORMAT(IFNULL(COMPLETE_DT, ''), '%Y-%m-%d'),'') as COMPLETE_DT, SATISFACTION FROM T_TICKET_INFO as T LEFT JOIN T_USER_INFO as U on T.MANAGER_ID = U.USER_ID
DELETE_YN = 'N'
AND T.COMPANY_ID = #{companyId}
AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))
AND STATUS_CD = #{searchStatus}
AND T.COMPANY_ID = #{searchCompany}
AND T.SUPPORT_CD = #{searchSupportCd}
AND TICKET_ID LIKE CONCAT('%', #{searchKeyword}, '%')
AND TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))
AND U.USER_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND T.CREATE_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND T.CREATE_DT BETWEEN #{startDt} AND #{endDt}
AND T.CREATE_ID = #{createId}
order by TICKET_ID desc
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM T_TICKET_INFO as T
LEFT JOIN T_USER_INFO as U on T.MANAGER_ID = U.USER_ID

    DELETE_YN = 'N'


        AND T.COMPANY_ID = #{companyId}



        AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
        OR CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))




        AND STATUS_CD = #{searchStatus}



        AND T.COMPANY_ID = #{searchCompany}


        AND T.SUPPORT_CD = #{searchSupportCd}





                AND TICKET_ID LIKE CONCAT('%', #{searchKeyword}, '%')  


                AND TITLE LIKE CONCAT('%', #{searchKeyword}, '%') 


                AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
                OR CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')) 


                AND U.USER_NAME LIKE CONCAT('%', #{searchKeyword}, '%')  


                AND T.CREATE_NAME LIKE CONCAT('%', #{searchKeyword}, '%')  





        AND T.CREATE_DT BETWEEN #{startDt} AND #{endDt}



        AND T.CREATE_ID = #{createId}

mapper.update:
UPDATE T_TICKET_INFO SET STATUS_CD = #{newStatus},
MANAGER_ID = #{updateId},
UPDATE_DT = NOW(), UPDATE_ID = #{updateId}
, COMPLETE_DT = NOW()
WHERE TICKET_ID = #{ticketId}

UPDATE T_TICKET_INFO
SET STATUS_CD = #{newStatus}, SATISFACTION = #{result}, FEEDBACK = #{feedback},
UPDATE_DT = NOW(),
UPDATE_ID = #{updateId}

    , COMPLETE_DT = NOW()

WHERE TICKET_ID = #{ticketId}

UPDATE T_TICKET_INFO
SET FILE_ID = #{fileId}
WHERE TICKET_ID = #{ticketId}

mapper.insert:
INSERT INTO T_TICKET_ANSWER
(TICKET_ID, STATUS_CD, CONTENT, CREATE_ID, CREATE_DT, UPDATE_DT, FILE_ID, DELETE_YN)
VALUES(#{ticketId}, #{statusCd}, #{content}, #{createId}, NOW(), NOW(), NULL, 'N');

mapper.update:
UPDATE T_TICKET_INFO
SET DELETE_YN = 'Y'
WHERE TICKET_ID = #{ticketId};

UPDATE T_TICKET_ANSWER
SET DELETE_YN = 'Y'
WHERE TICKET_ID = #{ticketId};

UPDATE T_TICKET_ANSWER
SET DELETE_YN = 'Y'
WHERE ANSWER_ID = #{Id};

UPDATE T_TICKET_ANSWER
SET FILE_ID = #{fileId}
WHERE ANSWER_ID = #{Id}

UPDATE T_FILE
SET DELETE_YN = 'Y'
WHERE FILE_ID = CONCAT('C',#{Id});
