mapper.select:
SELECT TICKET_ID, COMPANY_NAME, STATUS_CD, REQUEST_TYPE_CD, SUPPORT_CD, IFNULL(MD, '') as MD, TITLE, U.USER_NAME, DATE_FORMAT(T.CREATE_DT, '%Y-%m-%d') as CREATE_DT, IFNULL(DATE_FORMAT(IFNULL(REAL_COMPLETE_DT, ''), '%Y-%m-%d'),'') as REAL_COMPLETE_DT FROM T_TICKET_INFO as T LEFT JOIN T_USER_INFO as U on T.MANAGER_ID = U.USER_ID
DELETE_YN = 'N'
AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))
AND STATUS_CD = #{searchStatus}
AND TICKET_ID LIKE CONCAT('%', #{searchKeyword}, '%')
AND TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND (TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))
AND ASSIGNEE LIKE CONCAT('%', #{searchKeyword}, '%')
AND T.CREATE_DT BETWEEN #{startDt} AND #{endDt}
LIMIT #{offset}, #{size}
